package cernerPractice;

public class DecimalToBinary {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
                              
/*binary base */	System.out.println(Integer.toBinaryString(10));//converts the integer  parameter(value 10) to binary
/*to the base 16 */	System.out.println(Integer.toHexString(10));// converts the integer parameter to hexa decimal
/* to the base 8*/	System.out.println(Integer.toOctalString(10));// converts the integer parameter to octa decimal
	}

}
